import React, { useState } from 'react';
import { CardElement} from "@stripe/react-stripe-js";
import {
    useStripe,
    useElements
  } from "@stripe/react-stripe-js"
import './CheckoutForm.css';
import { stringify } from 'uuid';

function CheckoutForm({  totalCost }) {
  const [status, setStatus] = useState('default');
  const stripe = useStripe();
  const elements = useElements();
 console.log(stripe)
  const submit = async e => {
    e.preventDefault();
    const cardElement = elements.getElement(CardElement)
    setStatus('submitting');

    try {
      let  {paymentIntent, error}  = await stripe.confirmCardPayment({
          payment_method:{
              type:"card",
              card:cardElement,
              billing_details:{
                  name:""
              }
          }
      })

      let response = await fetch('/.netlify/functions/charge', {
        method: 'POST',
        body: JSON.stringify({
          amount: totalCost * 100,

        }),
      });

      if (response.ok) {
        setStatus('complete');
      } else {
        throw new Error('Network response was not ok.');
      }
    } catch (err) {
      setStatus('error');
      console.log(err.message)
      console.log(stripe)

    }
  };

  if (status === 'complete') {
    return <div className="CheckoutForm-complete">Payment successful!</div>;
  }

  return (
    <form className="CheckoutForm" onSubmit={submit}>
      <h4>Would you like to complete the purchase?</h4>
      <CardElement id="card-element"/>
      <button
        className="CheckoutForm-button"
        type="submit"
        disabled={status === 'submitting'}
      >
        {status === 'submitting' ? 'Submitting' : 'Submit Order'}
      </button>
      {status === 'error' && (
        <div className="CheckoutForm-error">Something went wrong.</div>
      )}
    </form>
  );
}

export default CheckoutForm;
